import React, { useState } from "react";
import { Container, Form, Button, Row, Col, Card } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Message from "../Message";
import { vendorLogin } from "../../actions/UserAction";
use
function LoginScreen() {
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [show, changeShow] = useState("fa fa-eye-slash"); // Toggle password visibility
    const [message, setMessage] = useState("");

    const userLogin = useSelector((state) => state.userLogin);
    const { loading, error, userInfo } = userLogin;

    const location = useLocation();
    const redirect = location.search ? location.search.split("=")[1] : "/";

    useEffect(() => {
        if (userInfo) {
            navigate(redirect);
        }
    }, [navigate, userInfo, redirect]);

    const submitHandler = (e) => {
        e.preventDefault();

        // Basic validation
        if (password.length < 6) {
            setMessage("Password should be at least 6 characters");
            return;
        }

        // Dispatch UserLogin action
        dispatch(vendorLogin(username, password));
    }




  return (
    <>
      <Container className="mt-3">
        <Row>
          <Col md={4}></Col>
          <Col md={4}>
            <Card>
              <Card.Header as="h3" className="text-center bg-black text-light">
                Login
              </Card.Header>
              <Card.Body>
                <Form>


                  <Form.Group className="mb-3"  controlId="username">
                    <Form.Label><span><i className="fa fa-username"></i></span>username</Form.Label>
                    <Form.Control type="text" placeholder="example@gmail.com" required/>
                  </Form.Group>
                  <Form.Group className="mb-3"  controlId="password">
                    <Form.Label><span><i className=" "></i></span>Password</Form.Label>
                    <Form.Control type="text" placeholder="Enter your Password" required/>
                  </Form.Group>

                  <div className='d-grid gap-2'>
                    <Button className="btn btn-md btn-success" type="submit">Sign</Button>
                  </div>
                </Form>
                <Row className="py-3">
                  <Col>New User ?
                  <Link to="/signup">Signup</Link>                  
                  </Col>
                </Row>


              </Card.Body>
            </Card>
          </Col>
          <Col md={4}></Col>
        </Row>
      </Container>
    </>
  );
}

export default LoginScreen;
